INSERT INTO `sl_guanyuwomen` VALUES ('1', '2017-11-04 01:37:09', '&lt;p style=&quot;white-space: normal; text-align: center;&quot;&gt;&lt;span style=&quot;font-size: 36px;&quot;&gt;&lt;strong&gt;爱华植保&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;&lt;span style=&quot;font-size: 24px;&quot;&gt;&lt;strong&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;&lt;span style=&quot;text-align: right; color: rgb(255, 255, 255); font-size: 20px; background-color: rgb(0, 176, 240);&quot;&gt;爱华植保&lt;/span&gt;&lt;span style=&quot;font-size: 20px;&quot;&gt;&lt;span style=&quot;text-align: right; color: rgb(255, 255, 255); background-color: rgb(0, 176, 240);&quot;&gt;&lt;strong style=&quot;font-size: 36px; text-align: center;&quot;&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;text-align: right; color: rgb(255, 255, 255); font-size: 20px; background-color: rgb(0, 176, 240);&quot;&gt;客户端&lt;/span&gt;&lt;strong style=&quot;text-align: right;&quot;&gt;&lt;span style=&quot;font-size: 36px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;版本号：1.3&lt;/p&gt;&lt;p class=&quot;p1&quot; style=&quot;white-space: normal;&quot;&gt;爱华植保APP归爱华农业开发有限公司所有。&lt;/p&gt;&lt;p class=&quot;p1&quot; style=&quot;white-space: normal;&quot;&gt;感谢爱华植保所有的贡献者！&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;&lt;span style=&quot;font-size: 20px; background-color: rgb(0, 176, 240); color: rgb(255, 255, 255);&quot;&gt;关于中国.爱华植保&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p class=&quot;p1&quot; style=&quot;white-space: normal;&quot;&gt;&amp;nbsp; &amp;nbsp;爱华农业开发有限公司诞生于2008年8月，公司聚合了多名从事农业科技的专业人士——其中有获得国务院政府津贴的农业专家龚海东老师；有专门从事农业技术推广的冯德美教授等。公司团队成员均有20年以上的项目经验,能够把握最本质的植保理念，结合互联网信息技术所带来的创新思维，致力于农作物和土壤的健康。&lt;br/&gt;&amp;nbsp; &amp;nbsp;我们为种植者提供免费的专业的植保技术服务；提供免费的农产品购销平台；提供优质价廉的农化产品。&lt;/p&gt;&lt;p class=&quot;p1&quot; style=&quot;white-space: normal;&quot;&gt;&amp;nbsp; &amp;nbsp;爱华农业开发有限公司已服务于除台湾地区以外的中国大陆3000余家种植专业合作社、基地、农场，并留下了较好的口碑。&lt;/p&gt;&lt;p class=&quot;p1&quot; style=&quot;white-space: normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p class=&quot;p1&quot; style=&quot;white-space: normal;&quot;&gt;&lt;embed type=&quot;application/x-shockwave-flash&quot; class=&quot;edui-faked-video&quot; pluginspage=&quot;http://www.macromedia.com/go/getflashplayer&quot; src=&quot;https://v.qq.com/x/page/q0375ep5j9k.html?ptag=qqbrowser&quot; width=&quot;420&quot; height=&quot;280&quot; wmode=&quot;transparent&quot; play=&quot;true&quot; loop=&quot;false&quot; menu=&quot;false&quot; allowscriptaccess=&quot;never&quot; allowfullscreen=&quot;true&quot;/&gt;&lt;/p&gt;', '关于我们');
