INSERT INTO `sl_huida` VALUES ('98', '2018-01-23 11:42:50', '5', '&lt;p&gt;考虑草莓灰霉病，推荐用药：凯泽+丁子香芹酚+纽萃钙硼喷雾，注意控制湿度，如果湿度大，可以用腐霉利烟熏剂，治病同时可以降低湿度。加强通风降低湿度，也可以短时间升温改变灰霉病的生存条件。&lt;br/&gt;&lt;/p&gt;', '2018012306346');
INSERT INTO `sl_huida` VALUES ('99', '2018-01-23 11:58:49', '5', '&lt;p&gt;考虑草莓灰霉病，注意加强通风降低湿度，对已经发病的部位及时摘除密闭带出棚外处理，避免传播！建议用凯泽+丁子香芹酚+纽萃钙硼喷雾，或者用腐霉利烟熏剂熏棚处理。&lt;/p&gt;', null);
